import Object = require('nashorn/java/lang/Object');
import PsiElement = require('nashorn/com/intellij/psi/PsiElement');
import PsiFile = require('nashorn/com/intellij/psi/PsiFile');
import CompletionType = require('nashorn/com/intellij/codeInsight/completion/CompletionType');
import Editor = require('nashorn/com/intellij/openapi/editor/Editor');

declare class CompletionParameters extends Object {
	position : PsiElement;
	originalPosition : PsiElement;
	originalFile : PsiFile;
	completionType : CompletionType;
	offset : number;
	invocationCount : number;
	autoPopup : boolean;
	extendedCompletion : boolean;
	editor : Editor;
	delegateToClassName() : CompletionParameters;
	withType(arg1 : CompletionType) : CompletionParameters;
	withInvocationCount(arg1 : number) : CompletionParameters;
	getPosition() : PsiElement;
	getOriginalPosition() : PsiElement;
	getOriginalFile() : PsiFile;
	getCompletionType() : CompletionType;
	getOffset() : number;
	getInvocationCount() : number;
	isAutoPopup() : boolean;
	withPosition(arg1 : PsiElement, arg2 : number) : CompletionParameters;
	isExtendedCompletion() : boolean;
	getEditor() : Editor;
}

export = CompletionParameters
