import PsiElement = require('nashorn/com/intellij/psi/PsiElement');
import Object = require('nashorn/java/lang/Object');
import Enum = require('nashorn/java/lang/Enum');

declare class Validator<T extends PsiElement> extends Object {
	validate(arg1 : T, arg2 : Validator.ValidationHost) : void;
}


declare module Validator {
	class ValidationHost extends Object {
		static WARNING : number;
		static ERROR : number;
		static INFO : number;
		addMessage(arg1 : PsiElement, arg2 : string, arg3 : number) : void;
		addMessage(arg1 : PsiElement, arg2 : string, arg3 : Validator.ValidationHost.ErrorType) : void;
	}


	module ValidationHost {
		class ErrorType {
			static WARNING : Validator.ValidationHost.ErrorType;
			static ERROR : Validator.ValidationHost.ErrorType;
			static INFO : Validator.ValidationHost.ErrorType;
		}


		interface ErrorType extends Enum<Validator.ValidationHost.ErrorType> {}	}
}

export = Validator
