import Object = require('nashorn/java/lang/Object');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import Editor = require('nashorn/com/intellij/openapi/editor/Editor');
import PsiFile = require('nashorn/com/intellij/psi/PsiFile');

declare class IntentionAction extends Object {
	static EMPTY_ARRAY : IntentionAction[];
	text : string;
	familyName : string;
	getText() : string;
	getFamilyName() : string;
	isAvailable(arg1 : Project, arg2 : Editor, arg3 : PsiFile) : boolean;
	invoke(arg1 : Project, arg2 : Editor, arg3 : PsiFile) : void;
	startInWriteAction() : boolean;
}

export = IntentionAction
