import Object = require('nashorn/java/lang/Object');
import Callable = require('nashorn/java/util/concurrent/Callable');
import Runnable = require('nashorn/java/lang/Runnable');
import List = require('nashorn/java/util/List');

declare class Job<T> extends Object {
	static DEFAULT_PRIORITY : number;
	static NULL_JOB : Job;
	title : string;
	canceled : boolean;
	done : boolean;
	getTitle() : string;
	addTask(arg1 : Callable<T>) : void;
	addTask(arg1 : Runnable, arg2 : T) : void;
	addTask(arg1 : Runnable) : void;
	scheduleAndWaitForResults() : List<T>;
	cancel() : void;
	isCanceled() : boolean;
	schedule() : void;
	isDone() : boolean;
	waitForCompletion(arg1 : number) : void;
}

export = Job
