import Object = require('nashorn/java/lang/Object');
import List = require('nashorn/java/util/List');
import Logger = require('nashorn/com/intellij/openapi/diagnostic/Logger');

declare class ProcessOutput extends Object {
	stdout : string;
	stderr : string;
	stdoutLines : List<string>;
	stderrLines : List<string>;
	exitCode : number;
	timeout : boolean;
	cancelled : boolean;
	constructor();
	constructor(arg1 : number);
	appendStdout(arg1 : string) : void;
	appendStderr(arg1 : string) : void;
	getStdout() : string;
	getStderr() : string;
	getStdoutLines() : List<string>;
	getStdoutLines(arg1 : boolean) : List<string>;
	getStderrLines() : List<string>;
	getStderrLines(arg1 : boolean) : List<string>;
	static splitLines(arg1 : string, arg2 : boolean) : List<string>;
	checkSuccess(arg1 : Logger) : boolean;
	setExitCode(arg1 : number) : void;
	getExitCode() : number;
	setTimeout() : void;
	isTimeout() : boolean;
	setCancelled() : void;
	isCancelled() : boolean;
}

export = ProcessOutput
