import RunnerSettings = require('nashorn/com/intellij/execution/configurations/RunnerSettings');
import ExecutionEnvironment = require('nashorn/com/intellij/execution/runners/ExecutionEnvironment');
import ProgramRunner = require('nashorn/com/intellij/execution/runners/ProgramRunner');
import RunProfileState = require('nashorn/com/intellij/execution/configurations/RunProfileState');
import RunContentDescriptor = require('nashorn/com/intellij/execution/ui/RunContentDescriptor');

declare class BaseProgramRunner<Settings extends RunnerSettings> {
	execute(arg1 : ExecutionEnvironment, arg2 : ProgramRunner.Callback, arg3 : RunProfileState) : void;
	static postProcess(arg1 : ExecutionEnvironment, arg2 : RunContentDescriptor, arg3 : ProgramRunner.Callback) : RunContentDescriptor;
}


declare interface BaseProgramRunner<Settings extends RunnerSettings> extends ProgramRunner<Settings> {}
export = BaseProgramRunner
