import Object = require('nashorn/java/lang/Object');
import Block = require('nashorn/com/intellij/formatting/Block');
import FormattingDocumentModel = require('nashorn/com/intellij/formatting/FormattingDocumentModel');
import TextRange = require('nashorn/com/intellij/openapi/util/TextRange');

declare class FormattingModel extends Object {
	rootBlock : Block;
	documentModel : FormattingDocumentModel;
	getRootBlock() : Block;
	getDocumentModel() : FormattingDocumentModel;
	replaceWhiteSpace(arg1 : TextRange, arg2 : string) : TextRange;
	shiftIndentInsideRange(arg1 : TextRange, arg2 : number) : TextRange;
	commitChanges() : void;
}

export = FormattingModel
