import Object = require('nashorn/java/lang/Object');
import IElementType = require('nashorn/com/intellij/psi/tree/IElementType');
import Lexer = require('nashorn/com/intellij/lexer/Lexer');

declare class HtmlScriptContentProvider extends Object {
	scriptElementType : IElementType;
	highlightingLexer : Lexer;
	getScriptElementType() : IElementType;
	getHighlightingLexer() : Lexer;
}


declare module HtmlScriptContentProvider {
	class Empty {
		scriptElementType : IElementType;
		highlightingLexer : Lexer;
		constructor();
	}


	interface Empty extends HtmlScriptContentProvider {}}

export = HtmlScriptContentProvider
