import Language = require('nashorn/com/intellij/lang/Language');
import List = require('nashorn/java/util/List');
import Key = require('nashorn/com/intellij/openapi/util/Key');
import KeyedExtensionCollector = require('nashorn/com/intellij/openapi/util/KeyedExtensionCollector');

declare class LanguageExtension<T> {
	constructor(arg1 : string);
	constructor(arg1 : string, arg2 : T);
	forLanguage(arg1 : Language) : T;
	allForLanguage(arg1 : Language) : List<T>;
	getDefaultImplementation() : T;
	getLanguageCache() : Key<T>;
}


declare interface LanguageExtension<T> extends KeyedExtensionCollector<T, Language> {}
export = LanguageExtension
