import Object = require('nashorn/java/lang/Object');
import Map = require('nashorn/java/util/Map');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import Collection = require('nashorn/java/util/Collection');

declare class PerFileMappings<T> extends Object {
	mappings : Map<VirtualFile, T>;
	getMappings() : Map<VirtualFile, T>;
	setMappings(arg1 : Map<VirtualFile, T>) : void;
	setMapping(arg1 : VirtualFile, arg2 : T) : void;
	getAvailableValues(arg1 : VirtualFile) : Collection<T>;
	getMapping(arg1 : VirtualFile) : T;
	getDefaultMapping(arg1 : VirtualFile) : T;
	chosenToStored(arg1 : VirtualFile, arg2 : T) : T;
	isSelectable(arg1 : T) : boolean;
}

export = PerFileMappings
