import Object = require('nashorn/java/lang/Object');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import Document = require('nashorn/com/intellij/openapi/editor/Document');
import Producer = require('nashorn/com/intellij/util/Producer');
import CharSequence = require('nashorn/java/lang/CharSequence');

declare class JSMinifiedFileUtil extends Object {
	constructor();
	static isFileMinified(arg1 : VirtualFile, arg2 : Project) : boolean;
	static isFileContentMinified(arg1 : VirtualFile) : boolean;
	static calcFileContentMinified(arg1 : VirtualFile, arg2 : Document) : boolean;
	static isFileContentMinified(arg1 : string, arg2 : Producer<CharSequence>) : boolean;
}


declare module JSMinifiedFileUtil {
	class CachedMinifiedStatus extends Object {
		minified : boolean;
		fileTimeStamp : number;
		documentTimeStamp : number;
		constructor(arg1 : boolean, arg2 : number, arg3 : number);
		isMinified() : boolean;
		getFileTimeStamp() : number;
		getDocumentTimeStamp() : number;
	}
}

export = JSMinifiedFileUtil
