import JSLinterState = require('nashorn/com/intellij/lang/javascript/linter/JSLinterState');
import Object = require('nashorn/java/lang/Object');
import JComponent = require('nashorn/javax/swing/JComponent');
import ExtendedLinterState = require('nashorn/com/intellij/lang/javascript/linter/ExtendedLinterState');
import Component = require('nashorn/java/awt/Component');
import JCheckBox = require('nashorn/javax/swing/JCheckBox');
import JPanel = require('nashorn/javax/swing/JPanel');

declare class JSLinterBaseView<T extends JSLinterState> extends Object {
	fullModeDialog : boolean;
	component : JComponent;
	extendedState : ExtendedLinterState<T>;
	isFullModeDialog() : boolean;
	createTopRightComponent() : Component;
	createCenterComponent() : Component;
	handleEnableStatusChanged(arg1 : boolean) : void;
	getState() : T;
	setState(arg1 : T) : void;
	getComponent() : JComponent;
	static createRootComponent(arg1 : Component, arg2 : Component) : JComponent;
	setExtendedState(arg1 : ExtendedLinterState<T>) : void;
	getExtendedState() : ExtendedLinterState<T>;
	getTopRightComponent() : Component;
	getCenterComponent() : Component;
	static createTopPanel(arg1 : JCheckBox, arg2 : Component) : JPanel;
	onEnableChanged() : void;
	disposeResources() : void;
}

export = JSLinterBaseView
