import Object = require('nashorn/java/lang/Object');
import OptionType = require('nashorn/com/intellij/lang/javascript/linter/option/OptionType');
import Option = require('nashorn/com/intellij/lang/javascript/linter/option/Option');
import OptionEnumType = require('nashorn/com/intellij/lang/javascript/linter/option/OptionEnumType');

declare class OptionTypes extends Object {
	static BOOLEAN : OptionType<boolean>;
	static INTEGER : OptionType<number>;
	static STRING : OptionType<string>;
	static ANY_VALUE : string;
	constructor();
	static isBooleanOption(arg1 : Option<any>) : boolean;
	static isStringOption(arg1 : Option<any>) : boolean;
	static isIntegerOption(arg1 : Option<any>) : boolean;
	static isEnumOption(arg1 : Option<any>) : boolean;
	static getOptionEnumType(arg1 : Option<any>) : OptionEnumType;
}


declare module OptionTypes {
	class StringOptionType {
	}


	interface StringOptionType extends OptionType<string> {}	class IntegerOptionType {
	}


	interface IntegerOptionType extends OptionType<number> {}	class BooleanOptionType {
	}


	interface BooleanOptionType extends OptionType<boolean> {}}

export = OptionTypes
