	getTypedefName() : string;
	getGenericParameters() : List<string>;
	getAccessType() : JSAttributeList.AccessType;
	getExplicitName() : string;
	getNamespace() : JSQualifiedName;
	isNamespaceExplicitlyDeclared() : boolean;
	getType() : string;
	getProperties() : Map<string, string>;
	isClassExplicitly() : boolean;
	hasInterfaceTag() : boolean;
	hasDeprecatedTag() : boolean;
	hasConstructsTag() : boolean;
	getJSContext() : JSContext;
	getBrowserFamily() : JSDocComment.BrowserFamily;
	hasNamespaceTag() : boolean;
