declare interface JSDocComment extends PsiComment, JSElement, JSImplicitElementProvider, PsiDocCommentBase {}

declare module JSDocComment {
	class BrowserFamily {
		static ANY : JSDocComment.BrowserFamily;
		static IE : JSDocComment.BrowserFamily;
		static OPERA : JSDocComment.BrowserFamily;
		static GECKO : JSDocComment.BrowserFamily;
	}


	interface BrowserFamily extends Enum<JSDocComment.BrowserFamily> {}}

export = JSDocComment
