import Object = require('nashorn/java/lang/Object');
import Icon = require('nashorn/javax/swing/Icon');
import Color = require('nashorn/java/awt/Color');
import ColorIcon = require('nashorn/com/intellij/util/ui/ColorIcon');

declare class FileColor extends Object {
	constructor();
	static getIcon(arg1 : string) : Icon;
	static getColor(arg1 : string) : Color;
	getFileIcon(arg1 : string) : Icon;
	getOrAddCachedItem(arg1 : string) : FileColor.MyColorAndIcon;
}


declare module FileColor {
	class MyColorAndIcon extends Object {
		icon : ColorIcon;
		color : Color;
		getIcon() : ColorIcon;
		getColor() : Color;
	}
}

export = FileColor
