import Object = require('nashorn/java/lang/Object');
import IElementType = require('nashorn/com/intellij/psi/tree/IElementType');
import CharSequence = require('nashorn/java/lang/CharSequence');

declare class FlexLexer extends Object {
	tokenStart : number;
	tokenEnd : number;
	yybegin(arg1 : number) : void;
	yystate() : number;
	getTokenStart() : number;
	getTokenEnd() : number;
	advance() : IElementType;
	reset(arg1 : CharSequence, arg2 : number, arg3 : number, arg4 : number) : void;
}

export = FlexLexer
