import Object = require('nashorn/java/lang/Object');
import Result = require('nashorn/com/intellij/openapi/application/Result');
import RunResult = require('nashorn/com/intellij/openapi/application/RunResult');
import Application = require('nashorn/com/intellij/openapi/application/Application');

declare class BaseActionRunnable<T> extends Object {
	silentExecution : boolean;
	constructor();
	isSilentExecution() : boolean;
	run(arg1 : Result<T>) : void;
	execute() : RunResult<T>;
	canWriteNow() : boolean;
	canReadNow() : boolean;
	getApplication() : Application;
	executeSilently() : RunResult<T>;
}

export = BaseActionRunnable
