import Object = require('nashorn/java/lang/Object');
import Runnable = require('nashorn/java/lang/Runnable');
import ActionCallback = require('nashorn/com/intellij/openapi/util/ActionCallback');
import Condition = require('nashorn/com/intellij/openapi/util/Condition');
import ModalityState = require('nashorn/com/intellij/openapi/application/ModalityState');

declare class ModalityInvokator extends Object {
	invokeLater(arg1 : Runnable) : ActionCallback;
	invokeLater(arg1 : Runnable, arg2 : Condition) : ActionCallback;
	invokeLater(arg1 : Runnable, arg2 : ModalityState) : ActionCallback;
	invokeLater(arg1 : Runnable, arg2 : ModalityState, arg3 : Condition) : ActionCallback;
}

export = ModalityInvokator
