import Document = require('nashorn/com/intellij/openapi/editor/Document');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import Runnable = require('nashorn/java/lang/Runnable');

declare class DocumentRunnable {
	document : Document;
	project : Project;
	constructor(arg1 : Document, arg2 : Project);
	getDocument() : Document;
	getProject() : Project;
}


declare interface DocumentRunnable extends Runnable {}

declare module DocumentRunnable {
	class IgnoreDocumentRunnable {
		constructor();
	}


	interface IgnoreDocumentRunnable extends DocumentRunnable {}}

export = DocumentRunnable
