import Object = require('nashorn/java/lang/Object');
import Rectangle = require('nashorn/java/awt/Rectangle');
import ScrollType = require('nashorn/com/intellij/openapi/editor/ScrollType');
import LogicalPosition = require('nashorn/com/intellij/openapi/editor/LogicalPosition');
import Runnable = require('nashorn/java/lang/Runnable');
import VisibleAreaListener = require('nashorn/com/intellij/openapi/editor/event/VisibleAreaListener');

declare class ScrollingModel extends Object {
	visibleArea : Rectangle;
	visibleAreaOnScrollingFinished : Rectangle;
	verticalScrollOffset : number;
	horizontalScrollOffset : number;
	getVisibleArea() : Rectangle;
	getVisibleAreaOnScrollingFinished() : Rectangle;
	scrollToCaret(arg1 : ScrollType) : void;
	scrollTo(arg1 : LogicalPosition, arg2 : ScrollType) : void;
	runActionOnScrollingFinished(arg1 : Runnable) : void;
	disableAnimation() : void;
	enableAnimation() : void;
	getVerticalScrollOffset() : number;
	getHorizontalScrollOffset() : number;
	scrollVertically(arg1 : number) : void;
	scrollHorizontally(arg1 : number) : void;
	addVisibleAreaListener(arg1 : VisibleAreaListener) : void;
	removeVisibleAreaListener(arg1 : VisibleAreaListener) : void;
}

export = ScrollingModel
