import Document = require('nashorn/com/intellij/openapi/editor/Document');
import CharSequence = require('nashorn/java/lang/CharSequence');
import EventObject = require('nashorn/java/util/EventObject');

declare class DocumentEvent {
	document : Document;
	offset : number;
	oldLength : number;
	newLength : number;
	oldFragment : CharSequence;
	newFragment : CharSequence;
	oldTimeStamp : number;
	wholeTextReplaced : boolean;
	getDocument() : Document;
	getOffset() : number;
	getOldLength() : number;
	getNewLength() : number;
	getOldFragment() : CharSequence;
	getNewFragment() : CharSequence;
	getOldTimeStamp() : number;
	isWholeTextReplaced() : boolean;
}


declare interface DocumentEvent extends EventObject {}
export = DocumentEvent
