import Icon = require('nashorn/javax/swing/Icon');
import ActionGroup = require('nashorn/com/intellij/openapi/actionSystem/ActionGroup');
import AnAction = require('nashorn/com/intellij/openapi/actionSystem/AnAction');
import GutterDraggableObject = require('nashorn/com/intellij/openapi/editor/markup/GutterDraggableObject');
import GutterMark = require('nashorn/com/intellij/codeInsight/daemon/GutterMark');
import Enum = require('nashorn/java/lang/Enum');

declare class GutterIconRenderer {
	icon : Icon;
	popupMenuActions : ActionGroup;
	tooltipText : string;
	clickAction : AnAction;
	middleButtonClickAction : AnAction;
	rightButtonClickAction : AnAction;
	navigateAction : boolean;
	alignment : GutterIconRenderer.Alignment;
	draggableObject : GutterDraggableObject;
	constructor();
	getPopupMenuActions() : ActionGroup;
	getClickAction() : AnAction;
	getMiddleButtonClickAction() : AnAction;
	getRightButtonClickAction() : AnAction;
	isNavigateAction() : boolean;
	getAlignment() : GutterIconRenderer.Alignment;
	getDraggableObject() : GutterDraggableObject;
}


declare interface GutterIconRenderer extends GutterMark {}

declare module GutterIconRenderer {
	class Alignment {
		static LEFT : GutterIconRenderer.Alignment;
		static RIGHT : GutterIconRenderer.Alignment;
		static CENTER : GutterIconRenderer.Alignment;
		weight : number;
		getWeight() : number;
	}


	interface Alignment extends Enum<GutterIconRenderer.Alignment> {}}

export = GutterIconRenderer
