import Object = require('nashorn/java/lang/Object');
import PluginDescriptor = require('nashorn/com/intellij/openapi/extensions/PluginDescriptor');

declare class ExtensionPointListener<T> extends Object {
	extensionAdded(arg1 : T, arg2 : PluginDescriptor) : void;
	extensionRemoved(arg1 : T, arg2 : PluginDescriptor) : void;
}


declare module ExtensionPointListener {
	class Adapter<T> {
		constructor();
	}


	interface Adapter<T> extends ExtensionPointListener<T> {}}

export = ExtensionPointListener
