import Icon = require('nashorn/javax/swing/Icon');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import Scheme = require('nashorn/com/intellij/openapi/options/Scheme');

declare class FileType {
	static EMPTY_ARRAY : FileType[];
	name : string;
	description : string;
	defaultExtension : string;
	icon : Icon;
	binary : boolean;
	readOnly : boolean;
	getDescription() : string;
	getDefaultExtension() : string;
	getIcon() : Icon;
	isBinary() : boolean;
	isReadOnly() : boolean;
	getCharset(arg1 : VirtualFile, arg2 : number[]) : string;
}


declare interface FileType extends Scheme {}
export = FileType
