import ExtensionPointName = require('nashorn/com/intellij/openapi/extensions/ExtensionPointName');
import ConfigurableEP = require('nashorn/com/intellij/openapi/options/ConfigurableEP');
import UnnamedConfigurable = require('nashorn/com/intellij/openapi/options/UnnamedConfigurable');
import Object = require('nashorn/java/lang/Object');

declare class Configurable {
	static APPLICATION_CONFIGURABLE : ExtensionPointName<ConfigurableEP<Configurable>>;
	static PROJECT_CONFIGURABLE : ExtensionPointName<ConfigurableEP<Configurable>>;
	displayName : string;
	helpTopic : string;
	getDisplayName() : string;
	getHelpTopic() : string;
}


declare interface Configurable extends UnnamedConfigurable {}

declare module Configurable {
	class NoMargin extends Object {
	}
	class NoScroll extends Object {
	}
	class Composite extends Object {
		configurables : Configurable[];
		getConfigurables() : Configurable[];
	}
	class Assistant {
	}


	interface Assistant extends Configurable {}}

export = Configurable
