import SdkTypeId = require('nashorn/com/intellij/openapi/projectRoots/SdkTypeId');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import RootProvider = require('nashorn/com/intellij/openapi/roots/RootProvider');
import SdkModificator = require('nashorn/com/intellij/openapi/projectRoots/SdkModificator');
import SdkAdditionalData = require('nashorn/com/intellij/openapi/projectRoots/SdkAdditionalData');
import UserDataHolder = require('nashorn/com/intellij/openapi/util/UserDataHolder');

declare class Sdk {
	sdkType : SdkTypeId;
	name : string;
	versionString : string;
	homePath : string;
	homeDirectory : VirtualFile;
	rootProvider : RootProvider;
	sdkModificator : SdkModificator;
	sdkAdditionalData : SdkAdditionalData;
	getSdkType() : SdkTypeId;
	getName() : string;
	getVersionString() : string;
	getHomePath() : string;
	getHomeDirectory() : VirtualFile;
	getRootProvider() : RootProvider;
	getSdkModificator() : SdkModificator;
	getSdkAdditionalData() : SdkAdditionalData;
}


declare interface Sdk extends UserDataHolder {}
export = Sdk
