import Object = require('nashorn/java/lang/Object');
import OrderEntry = require('nashorn/com/intellij/openapi/roots/OrderEntry');
import ModuleSourceOrderEntry = require('nashorn/com/intellij/openapi/roots/ModuleSourceOrderEntry');
import LibraryOrderEntry = require('nashorn/com/intellij/openapi/roots/LibraryOrderEntry');
import ModuleOrderEntry = require('nashorn/com/intellij/openapi/roots/ModuleOrderEntry');
import JdkOrderEntry = require('nashorn/com/intellij/openapi/roots/JdkOrderEntry');
import ModuleJdkOrderEntry = require('nashorn/com/intellij/openapi/roots/ModuleJdkOrderEntry');
import InheritedJdkOrderEntry = require('nashorn/com/intellij/openapi/roots/InheritedJdkOrderEntry');

declare class RootPolicy<R> extends Object {
	constructor();
	visitOrderEntry(arg1 : OrderEntry, arg2 : R) : R;
	visitModuleSourceOrderEntry(arg1 : ModuleSourceOrderEntry, arg2 : R) : R;
	visitLibraryOrderEntry(arg1 : LibraryOrderEntry, arg2 : R) : R;
	visitModuleOrderEntry(arg1 : ModuleOrderEntry, arg2 : R) : R;
	visitJdkOrderEntry(arg1 : JdkOrderEntry, arg2 : R) : R;
	visitModuleJdkOrderEntry(arg1 : ModuleJdkOrderEntry, arg2 : R) : R;
	visitInheritedJdkOrderEntry(arg1 : InheritedJdkOrderEntry, arg2 : R) : R;
}

export = RootPolicy
