import LibraryProperties = require('nashorn/com/intellij/openapi/roots/libraries/LibraryProperties');
import Object = require('nashorn/java/lang/Object');
import LibraryEditor = require('nashorn/com/intellij/openapi/roots/ui/configuration/libraryEditor/LibraryEditor');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import Project = require('nashorn/com/intellij/openapi/project/Project');

declare class LibraryEditorComponent<P extends LibraryProperties> extends Object {
	properties : P;
	newLibrary : boolean;
	libraryEditor : LibraryEditor;
	baseDirectory : VirtualFile;
	existingRootDirectory : VirtualFile;
	project : Project;
	getProperties() : P;
	isNewLibrary() : boolean;
	getLibraryEditor() : LibraryEditor;
	getBaseDirectory() : VirtualFile;
	getExistingRootDirectory() : VirtualFile;
	updateRootsTree() : void;
	getProject() : Project;
	renameLibrary(arg1 : string) : void;
}

export = LibraryEditorComponent
