import Object = require('nashorn/java/lang/Object');
import Icon = require('nashorn/javax/swing/Icon');
import Color = require('nashorn/java/awt/Color');
import NotificationType = require('nashorn/com/intellij/notification/NotificationType');

declare class MessageType extends Object {
	static ERROR : MessageType;
	static INFO : MessageType;
	static WARNING : MessageType;
	defaultIcon : Icon;
	popupBackground : Color;
	titleForeground : Color;
	getDefaultIcon() : Icon;
	getPopupBackground() : Color;
	getTitleForeground() : Color;
	toNotificationType() : NotificationType;
}

export = MessageType
