import Object = require('nashorn/java/lang/Object');
import Component = require('nashorn/java/awt/Component');

declare class NullableComponent extends Object {
	null : boolean;
	isNull() : boolean;
}


declare module NullableComponent {
	class Check extends Object {
		static isNull(arg1 : Component) : boolean;
		static isNullOrHidden(arg1 : Component) : boolean;
		static isNotNullAndVisible(arg1 : Component) : boolean;
	}
}

export = NullableComponent
