import Object = require('nashorn/java/lang/Object');
import Component = require('nashorn/java/awt/Component');
import Graphics2D = require('nashorn/java/awt/Graphics2D');
import JComponent = require('nashorn/javax/swing/JComponent');

declare class Painter extends Object {
	needsRepaint() : boolean;
	paint(arg1 : Component, arg2 : Graphics2D) : void;
	addListener(arg1 : Painter.Listener) : void;
	removeListener(arg1 : Painter.Listener) : void;
}


declare module Painter {
	class Listener extends Object {
		onNeedsRepaint(arg1 : Painter, arg2 : JComponent) : void;
	}
}

export = Painter
