import Component = require('nashorn/java/awt/Component');
import Object = require('nashorn/java/lang/Object');
import JTextField = require('nashorn/javax/swing/JTextField');
import JComboBox = require('nashorn/javax/swing/JComboBox');
import TextFieldWithHistory = require('nashorn/com/intellij/ui/TextFieldWithHistory');
import TextFieldWithStoredHistory = require('nashorn/com/intellij/ui/TextFieldWithStoredHistory');

declare class TextComponentAccessor<T extends Component> extends Object {
	static TEXT_FIELD_WHOLE_TEXT : TextComponentAccessor<JTextField>;
	static TEXT_FIELD_SELECTED_TEXT : TextComponentAccessor<JTextField>;
	static STRING_COMBOBOX_WHOLE_TEXT : TextComponentAccessor<JComboBox>;
	static TEXT_FIELD_WITH_HISTORY_WHOLE_TEXT : TextComponentAccessor<TextFieldWithHistory>;
	static TEXT_FIELD_WITH_STORED_HISTORY_WHOLE_TEXT : TextComponentAccessor<TextFieldWithStoredHistory>;
	getText(arg1 : T) : string;
	setText(arg1 : T, arg2 : string) : void;
}

export = TextComponentAccessor
