import Object = require('nashorn/java/lang/Object');
import MnemonicNavigationFilter = require('nashorn/com/intellij/openapi/ui/popup/MnemonicNavigationFilter');
import SpeedSearchFilter = require('nashorn/com/intellij/openapi/ui/popup/SpeedSearchFilter');
import Runnable = require('nashorn/java/lang/Runnable');

declare class PopupStep<T> extends Object {
	static FINAL_CHOICE : PopupStep;
	title : string;
	mnemonicsNavigationEnabled : boolean;
	mnemonicNavigationFilter : MnemonicNavigationFilter<T>;
	speedSearchEnabled : boolean;
	speedSearchFilter : SpeedSearchFilter<T>;
	autoSelectionEnabled : boolean;
	finalRunnable : Runnable;
	getTitle() : string;
	onChosen(arg1 : T, arg2 : boolean) : PopupStep;
	hasSubstep(arg1 : T) : boolean;
	canceled() : void;
	isMnemonicsNavigationEnabled() : boolean;
	getMnemonicNavigationFilter() : MnemonicNavigationFilter<T>;
	isSpeedSearchEnabled() : boolean;
	getSpeedSearchFilter() : SpeedSearchFilter<T>;
	isAutoSelectionEnabled() : boolean;
	getFinalRunnable() : Runnable;
}

export = PopupStep
