import Class = require('nashorn/java/lang/Class');
import Set = require('nashorn/java/util/Set');
import KeyedExtensionCollector = require('nashorn/com/intellij/openapi/util/KeyedExtensionCollector');

declare class ClassExtension<T> {
	constructor(arg1 : string);
	static collectSupers(arg1 : Class, arg2 : Set<string>) : void;
	forClass(arg1 : Class) : T;
}


declare interface ClassExtension<T> extends KeyedExtensionCollector<T, Class> {}
export = ClassExtension
