import Object = require('nashorn/java/lang/Object');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');

declare class VirtualFilePointer extends Object {
	static EMPTY_ARRAY : VirtualFilePointer[];
	fileName : string;
	file : VirtualFile;
	url : string;
	presentableUrl : string;
	valid : boolean;
	getFileName() : string;
	getFile() : VirtualFile;
	getUrl() : string;
	getPresentableUrl() : string;
	isValid() : boolean;
}

export = VirtualFilePointer
