import Object = require('nashorn/java/lang/Object');
import ElementPatternCondition = require('nashorn/com/intellij/patterns/ElementPatternCondition');
import ProcessingContext = require('nashorn/com/intellij/util/ProcessingContext');

declare class ElementPattern<T> extends Object {
	condition : ElementPatternCondition<T>;
	accepts(arg1 : Object) : boolean;
	accepts(arg1 : Object, arg2 : ProcessingContext) : boolean;
	getCondition() : ElementPatternCondition<T>;
}

export = ElementPattern
