import Object = require('nashorn/java/lang/Object');
import ASTNode = require('nashorn/com/intellij/lang/ASTNode');
import ChangeInfo = require('nashorn/com/intellij/pom/tree/events/ChangeInfo');

declare class TreeChange extends Object {
	affectedChildren : ASTNode[];
	empty : boolean;
	oldLength : number;
	addChange(arg1 : ASTNode, arg2 : ChangeInfo) : void;
	getAffectedChildren() : ASTNode[];
	getChangeByChild(arg1 : ASTNode) : ChangeInfo;
	getChildOffsetInNewTree(arg1 : ASTNode) : number;
	composite(arg1 : TreeChange) : void;
	isEmpty() : boolean;
	removeChange(arg1 : ASTNode) : void;
	add(arg1 : TreeChange) : void;
	getOldLength() : number;
}

export = TreeChange
