import ASTNode = require('nashorn/com/intellij/lang/ASTNode');
import TreeChange = require('nashorn/com/intellij/pom/tree/events/TreeChange');
import ChangeInfo = require('nashorn/com/intellij/pom/tree/events/ChangeInfo');
import PomChangeSet = require('nashorn/com/intellij/pom/event/PomChangeSet');

declare class TreeChangeEvent {
	rootElement : ASTNode;
	changedElements : ASTNode[];
	getRootElement() : ASTNode;
	getChangedElements() : ASTNode[];
	getChangesByElement(arg1 : ASTNode) : TreeChange;
	addElementaryChange(arg1 : ASTNode, arg2 : ChangeInfo) : void;
	clear() : void;
}


declare interface TreeChangeEvent extends PomChangeSet {}
export = TreeChangeEvent
