import PsiStatement = require('nashorn/com/intellij/psi/PsiStatement');
import PsiElement = require('nashorn/com/intellij/psi/PsiElement');
import PsiJavaToken = require('nashorn/com/intellij/psi/PsiJavaToken');
import PsiModifiableCodeBlock = require('nashorn/com/intellij/psi/PsiModifiableCodeBlock');

declare class PsiCodeBlock {
	static EMPTY_ARRAY : PsiCodeBlock[];
	statements : PsiStatement[];
	firstBodyElement : PsiElement;
	lastBodyElement : PsiElement;
	lBrace : PsiJavaToken;
	rBrace : PsiJavaToken;
	getStatements() : PsiStatement[];
	getFirstBodyElement() : PsiElement;
	getLastBodyElement() : PsiElement;
	getLBrace() : PsiJavaToken;
	getRBrace() : PsiJavaToken;
}


declare interface PsiCodeBlock extends PsiElement, PsiModifiableCodeBlock {}
export = PsiCodeBlock
