import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import PsiDirectory = require('nashorn/com/intellij/psi/PsiDirectory');
import FileType = require('nashorn/com/intellij/openapi/fileTypes/FileType');
import FileViewProvider = require('nashorn/com/intellij/psi/FileViewProvider');
import FileASTNode = require('nashorn/com/intellij/lang/FileASTNode');
import PsiFileSystemItem = require('nashorn/com/intellij/psi/PsiFileSystemItem');

declare class PsiFile {
	static EMPTY_ARRAY : PsiFile[];
	virtualFile : VirtualFile;
	containingDirectory : PsiDirectory;
	parent : PsiDirectory;
	modificationStamp : number;
	originalFile : PsiFile;
	fileType : FileType;
	psiRoots : PsiFile[];
	viewProvider : FileViewProvider;
	node : FileASTNode;
	getContainingDirectory() : PsiDirectory;
	getModificationStamp() : number;
	getOriginalFile() : PsiFile;
	getFileType() : FileType;
	getPsiRoots() : PsiFile[];
	getViewProvider() : FileViewProvider;
	subtreeChanged() : void;
}


declare interface PsiFile extends PsiFileSystemItem {}
export = PsiFile
