import PsiTypeElement = require('nashorn/com/intellij/psi/PsiTypeElement');
import PsiMember = require('nashorn/com/intellij/psi/PsiMember');
import PsiReferenceExpression = require('nashorn/com/intellij/psi/PsiReferenceExpression');
import PsiFunctionalExpression = require('nashorn/com/intellij/psi/PsiFunctionalExpression');

declare class PsiMethodReferenceExpression {
	qualifierType : PsiTypeElement;
	exact : boolean;
	potentiallyApplicableMember : PsiMember;
	getQualifierType() : PsiTypeElement;
	isExact() : boolean;
	getPotentiallyApplicableMember() : PsiMember;
	isConstructor() : boolean;
}


declare interface PsiMethodReferenceExpression extends PsiReferenceExpression, PsiFunctionalExpression {}
export = PsiMethodReferenceExpression
