import PsiCodeBlock = require('nashorn/com/intellij/psi/PsiCodeBlock');
import PsiParameter = require('nashorn/com/intellij/psi/PsiParameter');
import PsiCatchSection = require('nashorn/com/intellij/psi/PsiCatchSection');
import PsiResourceList = require('nashorn/com/intellij/psi/PsiResourceList');
import PsiStatement = require('nashorn/com/intellij/psi/PsiStatement');

declare class PsiTryStatement {
	tryBlock : PsiCodeBlock;
	catchBlocks : PsiCodeBlock[];
	catchBlockParameters : PsiParameter[];
	catchSections : PsiCatchSection[];
	finallyBlock : PsiCodeBlock;
	resourceList : PsiResourceList;
	getTryBlock() : PsiCodeBlock;
	getCatchBlocks() : PsiCodeBlock[];
	getCatchBlockParameters() : PsiParameter[];
	getCatchSections() : PsiCatchSection[];
	getFinallyBlock() : PsiCodeBlock;
	getResourceList() : PsiResourceList;
}


declare interface PsiTryStatement extends PsiStatement {}
export = PsiTryStatement
