import List = require('nashorn/java/util/List');
import TextRange = require('nashorn/com/intellij/openapi/util/TextRange');

declare class ReferenceRange extends Object {
	static getRanges(arg1 : PsiReference) : List<TextRange>;
	static getAbsoluteRanges(arg1 : PsiReference) : List<TextRange>;
	static getRange(arg1 : PsiReference) : TextRange;
	static containsOffsetInElement(arg1 : PsiReference, arg2 : number) : boolean;
	static containsRangeInElement(arg1 : PsiReference, arg2 : TextRange) : boolean;
}

export = ReferenceRange
