import PsiElement = require('nashorn/com/intellij/psi/PsiElement');
import Object = require('nashorn/java/lang/Object');
import PsiFile = require('nashorn/com/intellij/psi/PsiFile');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import VirtualFile = require('nashorn/com/intellij/openapi/vfs/VirtualFile');
import Segment = require('nashorn/com/intellij/openapi/util/Segment');

declare class SmartPsiElementPointer<E extends PsiElement> extends Object {
	element : E;
	containingFile : PsiFile;
	project : Project;
	virtualFile : VirtualFile;
	range : Segment;
	getElement() : E;
	getContainingFile() : PsiFile;
	getProject() : Project;
	getVirtualFile() : VirtualFile;
	getRange() : Segment;
}

export = SmartPsiElementPointer
