import Object = require('nashorn/java/lang/Object');
import Key = require('nashorn/com/intellij/openapi/util/Key');
import Weigher = require('nashorn/com/intellij/psi/Weigher');
import WeighingComparable = require('nashorn/com/intellij/psi/WeighingComparable');
import Computable = require('nashorn/com/intellij/openapi/util/Computable');
import List = require('nashorn/java/util/List');

declare class WeighingService extends Object {
	static weigh<T, Loc>(arg1 : Key<Weigher<T, Loc>>, arg2 : T, arg3 : Loc) : WeighingComparable<T, Loc>;
	static weigh<T, Loc>(arg1 : Key<Weigher<T, Loc>>, arg2 : Computable<T>, arg3 : Loc) : WeighingComparable<T, Loc>;
	static getWeighers<T, Loc>(arg1 : Key<Weigher<T, Loc>>) : List<Weigher>;
}

export = WeighingService
