import Object = require('nashorn/java/lang/Object');
import PackageSet = require('nashorn/com/intellij/psi/search/scope/packageSet/PackageSet');

declare class NamedScope extends Object {
	name : string;
	value : PackageSet;
	constructor(arg1 : string, arg2 : PackageSet);
	getName() : string;
	getValue() : PackageSet;
	createCopy() : NamedScope;
}


declare module NamedScope {
	class UnnamedScope {
		constructor(arg1 : PackageSet);
	}


	interface UnnamedScope extends NamedScope {}}

export = NamedScope
