import Throwable = require('nashorn/java/lang/Throwable');
import Object = require('nashorn/java/lang/Object');
import Class = require('nashorn/java/lang/Class');

declare class AbstractExceptionCase<T extends Throwable> extends Object {
	expectedExceptionClass : Class<T>;
	assertionErrorMessage : string;
	constructor();
	getExpectedExceptionClass() : Class<T>;
	tryClosure() : void;
	getAssertionErrorMessage() : string;
}

export = AbstractExceptionCase
