import Object = require('nashorn/java/lang/Object');
import JComponent = require('nashorn/javax/swing/JComponent');
import Content = require('nashorn/com/intellij/ui/content/Content');
import ContentUI = require('nashorn/com/intellij/ui/content/ContentUI');
import Project = require('nashorn/com/intellij/openapi/project/Project');
import ContentManager = require('nashorn/com/intellij/ui/content/ContentManager');

declare class ContentFactory extends Object {
	createContent(arg1 : JComponent, arg2 : string, arg3 : boolean) : Content;
	createContentManager(arg1 : ContentUI, arg2 : boolean, arg3 : Project) : ContentManager;
	createContentManager(arg1 : boolean, arg2 : Project) : ContentManager;
}


declare module ContentFactory {
	class SERVICE extends Object {
		static instance : ContentFactory;
		static getInstance() : ContentFactory;
	}
}

export = ContentFactory
