import Object = require('nashorn/java/lang/Object');
import Key = require('nashorn/com/intellij/openapi/util/Key');

declare class SharedProcessingContext extends Object {
	constructor();
	get(arg1 : string) : Object;
	put(arg1 : string, arg2 : Object) : void;
	put<T>(arg1 : Key<T>, arg2 : T) : void;
	get<T>(arg1 : Key<T>) : T;
	get<T>(arg1 : Key<T>, arg2 : Object) : T;
	put<T>(arg1 : Key<T>, arg2 : Object, arg3 : T) : void;
}

export = SharedProcessingContext
